import json
from base64 import b64encode, b64decode

from crypto_plus import CryptoPlus

# power[Result] 模板
power_result_template = 'EQUAL,{arg},65537,->{result}'


class JetbrainsKeyGen:

    def __init__(self):
        try:
            obj = CryptoPlus.load()
        except Exception:  # noqa
            obj = CryptoPlus.generate_rsa()
            obj.dump()
            obj.dump_cert(subject_name='Crack', issuer_name='JetProfile CA')
        self.crypto_plus = obj
        self.certificate_text = "".join(open('cert.crt').read().split(chr(10))[1:-2])
        self.certificate = CryptoPlus.loads(self.certificate_text).key
        self.license_data = json.load(open('licenses.json'))

    def generate(self, license_id=None, license_name=None):
        license_data = self.license_data.copy()
        if license_id:
            license_data['licenseId'] = license_id
        if license_name:
            license_data['licenseeName'] = license_name
        # 激活码组成
        license_id = license_data['licenseId']
        license_info = json.dumps(license_data, separators=(',', ':'))
        signature = self.crypto_plus.sign(license_info.encode(), "SHA1")
        activation_code = (
            f'{license_id}-'
            f'{b64encode(license_info.encode()).decode()}'
            f'-{b64encode(signature).decode()}-'
            f'{self.certificate_text}'
        )
        return activation_code

    def parse(self, licenses):
        license_id, license_info, signature, cert = licenses.split('-')
        license_info = b64decode(license_info)
        signature = b64decode(signature)
        certificate = CryptoPlus.loads(cert)
        assert certificate.verify(license_info, signature, "SHA1")
        license_info = json.loads(license_info.decode())
        assert license_id == license_info['licenseId']
        return license_info

    def patch(self):
        arg = int.from_bytes(self.certificate.signature, 'big')
        result = pow(arg, 65537, self.crypto_plus.public_key.n)
        return power_result_template.format(arg=arg, result=result)

    def run(self, patch=True):
        ciphertext_licenses = self.generate()
        print(f"ciphertext_licenses: \n{ciphertext_licenses}")
        if patch:
            patch_info = self.patch()
            if patch_info:
                print(f"patch: \n{patch_info}")
        plaintext_licenses = self.parse(ciphertext_licenses)
        print(f'plaintext_licenses: \n{plaintext_licenses}')


if __name__ == '__main__':
    from plugins import JetBrainPlugin

    JetBrainPlugin().update().make_licenses()
    JetbrainsKeyGen().run()
